script:
jQuery(document).ready(function(){

var splitter =  document.getElementById('splitter_bar');
splitter.style.width = document.body.clientWidth + "px";
splitter.style.height = "600px";

jQuery(".splitter_bar").splitter({
  type: "v",
  outline: true,
  minLeft: 1, sizeLeft: 150, minRight: 100,
  resizeToWidth: true,
  cookie: "vsplitter",
  accessKey: 'I'
});

});
